<?php

namespace app\helpers;

class CryptoHelper
{
    const CurrencyNameList = [
        'ETH' => [
            'name' => 'Ethereum',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'KNC' => [
            'name' => 'KyberNetwork',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'OMG' => [
            'name' => 'OmiseGO',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'EOS' => [
            'name' => 'Eos',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'SNT' => [
            'name' => 'Status',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'ELF' => [
            'name' => 'Aelf',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'POWR' => [
            'name' => 'Power Ledger',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'MANA' => [
            'name' => 'Mana',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'BAT' => [
            'name' => 'Basic Attention Token',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'REQ' => [
            'name' => 'Request',
            'icon' => 'data:image/svg+xml;base64,',
        ],
        'GTO' => [
            'name' => 'Gifto',
            'icon' => 'data:image/svg+xml;base64,',
        ],
    ];

    public static function getJsCurrencyNameList()
    {
        $items = [];
        foreach (self::CurrencyNameList as $shortName => $vals) {
            $items[] = $shortName . ":{name:'$vals[name]',icon:'$vals[icon]'}";
        }

        return '{' . implode(',', $items) . '}';
    }

    public static function convertCurrenciesByExchangeRatesOnline($amount, string $currencyFrom, string $currencyTo)
    {
        $result = false;

        if ($info = file_get_contents("https://api.cryptonator.com/api/ticker/$currencyFrom-$currencyTo")) {
            if ($infoDecoded = json_decode($info, true)) {
                if (!empty($infoDecoded['success']) && isset($infoDecoded['ticker']['price'])) {
                    $result = $amount * $infoDecoded['ticker']['price'];
                }
            }
        }

        return $result;
    }
}
